mapper.update:
update menu
set text = #{text,jdbcType=VARCHAR},
  url = #{url,jdbcType=VARCHAR},
  parent_id = #{parentId,jdbcType=BIGINT},
  permission_id = #{permissionId,jdbcType=BIGINT}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, text, url, parent_id, permission_id
from menu
where id = #{id,jdbcType=BIGINT}
